import { useState } from 'react';

// 生成模拟数据: 一年的日常贡献活动
const generateMockData = () => {
    const today = new Date();
    const oneYearAgo = new Date(today);
    oneYearAgo.setFullYear(today.getFullYear() - 1);

    const days = [];
    const current = new Date(oneYearAgo);

    // 随机生成一年的贡献数据
    while (current <= today) {
        const date = new Date(current);

        // 生成一个0-30之间的随机数作为当天的贡献数
        // 周末和工作日贡献量有所不同
        const isWeekend = date.getDay() === 0 || date.getDay() === 6;
        const maxContributions = isWeekend ? 15 : 30;
        const contributions = Math.floor(Math.random() * maxContributions);

        days.push({
            date: date.toISOString().split('T')[0],
            count: contributions,
            weekday: date.getDay(),
            month: date.getMonth()
        });

        // 增加一天
        current.setDate(current.getDate() + 1);
    }

    // 计算总贡献数和平均每日贡献数
    const totalContributions = days.reduce((sum, day) => sum + day.count, 0);
    const averageDailyContributions = (totalContributions / days.length).toFixed(1);

    // 找出贡献最多的一天
    const mostActiveDay = days.reduce((max, day) => day.count > max.count ? day : max, days[0]);

    // 计算每周的贡献
    const weeks = [];
    for (let i = 0; i < days.length; i += 7) {
        const weekDays = days.slice(i, i + 7);
        const weekTotal = weekDays.reduce((sum, day) => sum + day.count, 0);
        weeks.push({
            startDate: weekDays[0].date,
            endDate: weekDays[weekDays.length - 1].date,
            total: weekTotal
        });
    }

    return {
        days,
        totalContributions,
        averageDailyContributions,
        mostActiveDay,
        weeks
    };
};

const ActivityHeatmap = () => {
    const [data] = useState(generateMockData());

    // 根据贡献数计算热图单元格的颜色
    const getHeatmapColor = (count) => {
        if (count === 0) return '#ebedf0';
        if (count < 5) return '#9be9a8';
        if (count < 10) return '#40c463';
        if (count < 20) return '#30a14e';
        return '#216e39';
    };

    // 将日期格式化为可读形式
    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleDateString('zh-CN', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
        });
    };

    // 计算每月的位置，用于显示月份标签
    const getMonthLabels = () => {
        const months = [];
        let currentMonth = -1;

        data.days.forEach((day, index) => {
            const month = new Date(day.date).getMonth();
            if (month !== currentMonth) {
                months.push({
                    name: new Date(day.date).toLocaleDateString('zh-CN', { month: 'short' }),
                    index
                });
                currentMonth = month;
            }
        });

        return months;
    };

    return (
        <div className="activity-heatmap-page">
            <h1 className="page-title">活跃度热图</h1>

            <div className="stats-overview">
                <div className="card stat-card">
                    <div className="stat-value">{data.totalContributions.toLocaleString()}</div>
                    <div className="stat-label">总贡献数</div>
                </div>
                <div className="card stat-card">
                    <div className="stat-value">{data.averageDailyContributions}</div>
                    <div className="stat-label">日均贡献</div>
                </div>
                <div className="card stat-card">
                    <div className="stat-value">{formatDate(data.mostActiveDay.date)}</div>
                    <div className="stat-label">最活跃的一天</div>
                    <div className="stat-description">{data.mostActiveDay.count} 次贡献</div>
                </div>
            </div>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">年度贡献热图</h2>
                </div>
                <div className="card-body">
                    <div className="heatmap-container">
                        <div className="weekday-labels">
                            <div className="weekday-label">周日</div>
                            <div className="weekday-label">周一</div>
                            <div className="weekday-label">周二</div>
                            <div className="weekday-label">周三</div>
                            <div className="weekday-label">周四</div>
                            <div className="weekday-label">周五</div>
                            <div className="weekday-label">周六</div>
                        </div>

                        <div className="heatmap-grid">
                            {data.days.map((day) => (
                                <div
                                    key={day.date}
                                    className="heatmap-cell"
                                    style={{
                                        backgroundColor: getHeatmapColor(day.count),
                                        gridColumn: day.weekday + 1,
                                        gridRow: Math.floor(data.days.indexOf(day) / 7) + 1
                                    }}
                                    title={`${formatDate(day.date)}: ${day.count} 次贡献`}
                                />
                            ))}

                            <div className="month-labels">
                                {getMonthLabels().map(month => (
                                    <div
                                        key={month.name}
                                        className="month-label"
                                        style={{ gridColumnStart: Math.floor(month.index / 7) + 1 }}
                                    >
                                        {month.name}
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>

                    <div className="heatmap-legend">
                        <div className="legend-label">较少</div>
                        {[0, 4, 9, 19, 30].map(value => (
                            <div
                                key={value}
                                className="legend-cell"
                                style={{ backgroundColor: getHeatmapColor(value) }}
                            />
                        ))}
                        <div className="legend-label">较多</div>
                    </div>
                </div>
            </div>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">最近活动</h2>
                </div>
                <div className="card-body">
                    <div className="recent-activity">
                        <div className="activity-chart">
                            {data.weeks.slice(-10).map(week => (
                                <div key={week.startDate} className="week-bar">
                                    <div className="week-date">
                                        {formatDate(week.startDate)}
                                    </div>
                                    <div className="bar-container">
                                        <div
                                            className="bar-fill"
                                            style={{
                                                width: `${(week.total / 150) * 100}%`,
                                                backgroundColor: '#3b82f6'
                                            }}
                                        ></div>
                                        <span className="bar-value">
                                            {week.total}
                                        </span>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ActivityHeatmap;
