import { useState } from 'react';

const mockData = {
    companies: [
        { name: 'Google', count: 2473, percentage: 8.6 },
        { name: 'Microsoft', count: 2284, percentage: 7.9 },
        { name: 'Facebook', count: 1842, percentage: 6.4 },
        { name: 'Amazon', count: 1573, percentage: 5.5 },
        { name: 'Apple', count: 1342, percentage: 4.7 },
        { name: 'IBM', count: 1128, percentage: 3.9 },
        { name: 'Tencent', count: 983, percentage: 3.4 },
        { name: 'Alibaba', count: 872, percentage: 3.0 },
        { name: 'Red Hat', count: 763, percentage: 2.7 },
        { name: 'Intel', count: 682, percentage: 2.4 },
        { name: 'ByteDance', count: 573, percentage: 2.0 },
        { name: 'Oracle', count: 524, percentage: 1.8 },
        { name: 'JetBrains', count: 482, percentage: 1.7 },
        { name: 'Twitter', count: 437, percentage: 1.5 },
        { name: '其他', count: 12852, percentage: 44.5 }
    ],
    totalContributors: 28810,
    domains: [
        { name: '企业', count: 17326, percentage: 60.1 },
        { name: '独立开发者', count: 6824, percentage: 23.7 },
        { name: '学术', count: 2678, percentage: 9.3 },
        { name: '非营利组织', count: 1982, percentage: 6.9 }
    ]
};

const CompanyDistribution = () => {
    const [data] = useState(mockData);

    // 生成渐变色
    const getCompanyColor = (index, total) => {
        const hue = 210; // 蓝色调
        const saturation = '75%';
        const minLightness = 35;
        const maxLightness = 65;

        // 根据索引计算亮度值，使颜色由深到浅
        const lightness = maxLightness - ((index / Math.min(total - 1, 12)) * (maxLightness - minLightness));

        return `hsl(${hue}, ${saturation}, ${lightness}%)`;
    };

    // 对于领域图表使用不同颜色
    const getDomainColor = (index) => {
        const colors = ['#4299e1', '#48bb78', '#ed8936', '#f56565'];
        return colors[index % colors.length];
    };

    return (
        <div className="company-distribution-page">
            <h1 className="page-title">公司分布</h1>

            <div className="stats-overview">
                <div className="card stat-card">
                    <div className="stat-value">{data.totalContributors.toLocaleString()}</div>
                    <div className="stat-label">企业贡献者</div>
                </div>
                <div className="card stat-card">
                    <div className="stat-value">{data.companies.length - 1}</div>
                    <div className="stat-label">主要公司</div>
                </div>
                <div className="card stat-card">
                    <div className="stat-value">{data.companies[0].name}</div>
                    <div className="stat-label">贡献最多的公司</div>
                    <div className="stat-description">{data.companies[0].count.toLocaleString()} 名贡献者</div>
                </div>
            </div>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">公司分布图</h2>
                </div>
                <div className="card-body">
                    <div className="company-chart">
                        <div className="chart-container">
                            {/* 在实际项目中使用Recharts等图表库实现饼图 */}
                            <div className="mock-pie-chart">
                                {data.companies.filter((_, index) => index < 10).map((company, index) => (
                                    <div
                                        key={company.name}
                                        className="pie-segment"
                                        style={{
                                            backgroundColor: getCompanyColor(index, data.companies.length),
                                            transform: `rotate(${index * 36}deg)`,
                                            zIndex: 10 - index
                                        }}
                                    >
                                        <span className="segment-label">
                                            {company.name}
                                        </span>
                                    </div>
                                ))}
                            </div>
                        </div>

                        <div className="chart-legend">
                            {data.companies.map((company, index) => (
                                <div key={company.name} className="legend-item">
                                    <div
                                        className="color-indicator"
                                        style={{ backgroundColor: getCompanyColor(index, data.companies.length) }}
                                    ></div>
                                    <div className="company-info">
                                        <span className="company-name">{company.name}</span>
                                        <span className="company-percentage">{company.percentage}%</span>
                                    </div>
                                    <div className="company-count">{company.count.toLocaleString()} 名贡献者</div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">贡献者领域分布</h2>
                </div>
                <div className="card-body">
                    <div className="domains-chart">
                        {data.domains.map((domain, index) => (
                            <div key={domain.name} className="domain-bar">
                                <div className="domain-header">
                                    <div className="domain-name">{domain.name}</div>
                                    <div className="domain-percentage">{domain.percentage}%</div>
                                </div>
                                <div className="bar-container">
                                    <div
                                        className="bar-fill"
                                        style={{
                                            width: `${domain.percentage}%`,
                                            backgroundColor: getDomainColor(index)
                                        }}
                                    ></div>
                                </div>
                                <div className="domain-count">{domain.count.toLocaleString()} 名贡献者</div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">公司贡献热图</h2>
                </div>
                <div className="card-body">
                    <div className="heat-map-placeholder">
                        <div className="heat-map-message">
                            在实际项目中，这里可以展示一个公司贡献热图，
                            显示世界各个地区公司贡献者的密度分布。
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CompanyDistribution;
