// Helper function for language color (can be moved to a utils file)
const getLanguageColor = (language) => {
    const colorMap = {
        JavaScript: '#f1e05a', TypeScript: '#2b7489', Python: '#3572A5',
        Java: '#b07219', Go: '#00ADD8', 'C++': '#f34b7d', Ruby: '#701516',
        PHP: '#4F5D95', Rust: '#dea584', 'C#': '#178600', Swift: '#ffac45',
        Kotlin: '#F18E33', C: '#555555', Shell: '#89e051', HTML: '#e34c26',
        CSS: '#563d7c'
    };
    return colorMap[language] || '#cccccc';
};

// *** NEW: Helper function for Linear Regression ***
function calculateLinearRegression(points) {
    // points should be an array of { x: number, y: number } representing log-percentage positions
    if (!points || points.length < 2) {
        return null; // Not enough points to calculate regression
    }

    let sumX = 0, sumY = 0, sumXY = 0, sumX2 = 0;
    const n = points.length;

    for (const p of points) {
        // Ensure points are valid numbers before summing
        if (!isFinite(p.x) || !isFinite(p.y)) return null;
        sumX += p.x;
        sumY += p.y;
        sumXY += p.x * p.y;
        sumX2 += p.x * p.x;
    }

    const denominator = n * sumX2 - sumX * sumX;
    if (Math.abs(denominator) < 1e-6) { // Avoid division by near-zero (vertical line case)
        return null;
    }

    const slope = (n * sumXY - sumX * sumY) / denominator;
    const intercept = (sumY - slope * sumX) / n;

    // Check if slope or intercept are NaN/Infinite
    if (!isFinite(slope) || !isFinite(intercept)) {
        return null;
    }

    return { slope, intercept };
}

// *** NEW: Helper to generate points along the linear regression line ***
function generateLinePoints(regression, numPoints = 20, minX = 2, maxX = 98) {
    if (!regression) return [];
    const points = [];
    const step = (maxX - minX) / (numPoints - 1);
    for (let i = 0; i < numPoints; i++) {
        const x = minX + i * step;
        const y = regression.slope * x + regression.intercept;
        // Ensure y is within reasonable bounds (e.g., 0-100+) before coordinate flip
        if (isFinite(y)) {
            // Flip Y for SVG coordinates (0 is top)
            points.push({ x, y: 100 - y });
        }
    }
    return points;
}

// *** NEW: Helper to create SVG path data string (polyline) ***
function createSvgPath(points) {
    if (!points || points.length === 0) return "";
    // Start with MoveTo command for the first point
    let pathData = `M ${points[0].x},${points[0].y}`;
    // Add LineTo commands for subsequent points
    for (let i = 1; i < points.length; i++) {
        pathData += ` L ${points[i].x},${points[i].y}`;
    }
    return pathData;
}

