import { useState, useEffect } from 'react';
import API from '../config/api';

// 根据语言名称生成颜色
const getLanguageColor = (language) => {
    const colorMap = {
        JavaScript: '#f1e05a',
        TypeScript: '#2b7489',
        Python: '#3572A5',
        Java: '#b07219',
        Go: '#00ADD8',
        'C++': '#f34b7d',
        Ruby: '#701516',
        PHP: '#4F5D95',
        Rust: '#dea584',
        'C#': '#178600',
        Swift: '#ffac45',
        Kotlin: '#F18E33'
    };

    return colorMap[language] ||
        `hsl(${Math.abs(language.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0)) % 360}, 70%, 60%)`;
};

const LanguageDistribution = () => {
    const [data, setData] = useState({
        languages: [],
        totalRepositories: 0
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        async function fetchData() {
            try {
                setLoading(true);
                const response = await fetch(API.languageDistribution);
                if (!response.ok) throw new Error('获取数据失败');
                const result = await response.json();
                setData(result);
            } catch (err) {
                setError(err.message);
                console.error('获取语言分布出错:', err);
            } finally {
                setLoading(false);
            }
        }

        fetchData();
    }, []);

    if (loading) return <div className="loading-indicator">数据加载中...</div>;
    if (error) return <div className="error-message">加载出错: {error}</div>;

    return (
        <div className="language-distribution-page">
            <h1 className="page-title">编程语言分布</h1>

            <div className="stats-overview">
                <div className="card stat-card">
                    <div className="stat-value">{data.totalRepositories.toLocaleString()}</div>
                    <div className="stat-label">仓库总数</div>
                </div>
                <div className="card stat-card">
                    <div className="stat-value">{data.languages.length}</div>
                    <div className="stat-label">主要语言</div>
                </div>
                {data.languages.length > 0 && (
                    <div className="card stat-card">
                        <div className="stat-value">{data.languages[0].name}</div>
                        <div className="stat-label">最流行语言</div>
                        <div className="stat-description">{data.languages[0].percentage}% 的仓库</div>
                    </div>
                )}
            </div>

            <div className="dashboard-row">
                <div className="card">
                    <div className="card-header">
                        <h2 className="card-title">语言分布图</h2>
                    </div>
                    <div className="card-body">
                        <div className="chart-container">
                            {/* 在实际项目中，这里会使用Recharts、Chart.js等库实现饼图 */}
                            <div className="mock-pie-chart">
                                {data.languages.slice(0, 8).map((language, index) => (
                                    <div
                                        key={language.name}
                                        className="pie-segment"
                                        style={{
                                            backgroundColor: language.color,
                                            transform: `rotate(${index * 45}deg)`,
                                            zIndex: 10 - index
                                        }}
                                    >
                                        <span className="segment-label">
                                            {language.name}
                                        </span>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">按语言比较</h2>
                </div>
                <div className="card-body">
                    <div className="horizontal-bars">
                        {data.languages.map(language => (
                            <div key={language.name} className="horizontal-bar-item">
                                <div className="bar-label">{language.name}</div>
                                <div className="bar-container">
                                    <div
                                        className="bar-fill"
                                        style={{
                                            width: `${language.percentage}%`,
                                            backgroundColor: language.color
                                        }}
                                    ></div>
                                </div>
                                <div className="bar-value">{language.count.toLocaleString()} ({language.percentage}%)</div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">语言列表</h2>
                </div>
                <div className="card-body">
                    <div className="language-list">
                        {data.languages.map(language => (
                            <div key={language.name} className="language-item">
                                <div className="language-info">
                                    <div className="language-name">
                                        <span
                                            className="language-indicator"
                                            style={{ backgroundColor: language.color }}
                                        ></span>
                                        {language.name}
                                    </div>
                                    <div className="language-count">{language.count.toLocaleString()} 个仓库</div>
                                </div>
                                <div className="language-bar">
                                    <div
                                        className="language-progress"
                                        style={{
                                            width: `${language.percentage}%`,
                                            backgroundColor: language.color
                                        }}
                                    ></div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LanguageDistribution;
