import { useState } from 'react';

const mockData = {
    languages: [
        { name: 'JavaScript', count: 1542, percentage: 28.4 },
        { name: 'Python', count: 1198, percentage: 22.1 },
        { name: 'Java', count: 852, percentage: 15.7 },
        { name: 'TypeScript', count: 668, percentage: 12.3 },
        { name: 'Go', count: 483, percentage: 8.9 },
        { name: 'C++', count: 391, percentage: 7.2 },
        { name: 'Ruby', count: 293, percentage: 5.4 }
    ]
};

// 根据语言名称生成颜色
const getLanguageColor = (language) => {
    const colorMap = {
        JavaScript: '#f1e05a',
        TypeScript: '#2b7489',
        Python: '#3572A5',
        Java: '#b07219',
        Go: '#00ADD8',
        'C++': '#f34b7d',
        Ruby: '#701516',
        PHP: '#4F5D95',
        Rust: '#dea584',
        'C#': '#178600',
        Swift: '#ffac45',
        Kotlin: '#F18E33'
    };

    return colorMap[language] ||
        `hsl(${Math.abs(language.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0)) % 360}, 70%, 60%)`;
};

const LanguageDistribution = () => {
    const [data] = useState(mockData);

    return (
        <div className="language-distribution-page">
            <h1 className="page-title">编程语言分布</h1>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">语言使用比例</h2>
                </div>
                <div className="card-body">
                    <div className="pie-chart-placeholder">
                        {/* 实际项目中这里可以使用Recharts等图表库绘制饼图 */}
                        <div className="mock-pie-chart">
                            {data.languages.map((lang, index) => (
                                <div
                                    key={lang.name}
                                    className="pie-segment"
                                    style={{
                                        backgroundColor: getLanguageColor(lang.name),
                                        transform: `rotate(${index * 51.4}deg)`,
                                        zIndex: data.languages.length - index
                                    }}
                                >
                                    <span className="segment-label">
                                        {lang.name}
                                    </span>
                                </div>
                            ))}
                        </div>
                    </div>

                    <div className="language-legend">
                        {data.languages.map(lang => (
                            <div key={lang.name} className="legend-item">
                                <div
                                    className="color-indicator"
                                    style={{ backgroundColor: getLanguageColor(lang.name) }}
                                ></div>
                                <div className="legend-info">
                                    <div className="legend-name">{lang.name}</div>
                                    <div className="legend-stats">
                                        <span className="legend-percentage">{lang.percentage}%</span>
                                        <span className="legend-count">({lang.count}个仓库)</span>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">按语言比较</h2>
                </div>
                <div className="card-body">
                    <div className="horizontal-bars">
                        {data.languages.map(lang => (
                            <div key={lang.name} className="horizontal-bar-item">
                                <div className="bar-label">{lang.name}</div>
                                <div className="bar-container">
                                    <div
                                        className="bar-fill"
                                        style={{
                                            width: `${lang.percentage}%`,
                                            backgroundColor: getLanguageColor(lang.name)
                                        }}
                                    ></div>
                                </div>
                                <div className="bar-value">{lang.count}</div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LanguageDistribution;
