import { useState, useEffect } from 'react';
import API from '../config/api';

// Ê†πÊçÆËØ≠Ë®ÄÂêçÁß∞ÁîüÊàêÈ¢úËâ≤
const getLanguageColor = (language) => {
    const colorMap = {
        JavaScript: '#f1e05a',
        TypeScript: '#2b7489',
        Python: '#3572A5',
        Java: '#b07219',
        Go: '#00ADD8',
        'C++': '#f34b7d',
        Ruby: '#701516',
        PHP: '#4F5D95',
        Rust: '#dea584',
        'C#': '#178600',
        Swift: '#ffac45',
        Kotlin: '#F18E33',
        'C': '#555555',
        'Shell': '#89e051',
        'HTML': '#e34c26',
        'CSS': '#563d7c',
    };
    if (colorMap[language]) {
        return colorMap[language];
    }
    const hue = Math.abs(language.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0)) % 360;
    return `hsl(${hue}, 65%, 65%)`;
};

const Dashboard = () => {
    const [stats, setStats] = useState({ totalRepositories: 0, totalStars: 0, totalContributors: 0 });
    const [languageData, setLanguageData] = useState([]);
    const [topRepos, setTopRepos] = useState({});
    const [hoveredLanguage, setHoveredLanguage] = useState(null);
    const [loadingStats, setLoadingStats] = useState(true);
    const [loadingLanguages, setLoadingLanguages] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchDashboardData = async () => {
            try {
                setStats({ totalRepositories: 5427, totalStars: 3641982, totalContributors: 48273 });
                setLoadingStats(false);

                setLoadingLanguages(true);
                const langResponse = await fetch(API.languageDistribution);
                if (!langResponse.ok) {
                    throw new Error(`Ëé∑ÂèñËØ≠Ë®ÄÊï∞ÊçÆÂ§±Ë¥•: ${langResponse.status}`);
                }
                const langResult = await langResponse.json();
                setLanguageData(langResult);

                const languageTopRepos = {};
                const topLanguages = langResult.slice(0, 6);
                for (const lang of topLanguages) {
                    try {
                        const reposResponse = await fetch(`${API.languageTopRepos}?language=${encodeURIComponent(lang.language)}`);
                        if (reposResponse.ok) {
                            const reposData = await reposResponse.json();
                            languageTopRepos[lang.language] = reposData;
                        }
                    } catch (repoError) {
                        console.warn(`Ëé∑Âèñ ${lang.language} ÁÉ≠Èó®‰ªìÂ∫ìÂ§±Ë¥•:`, repoError);
                    }
                }
                setTopRepos(languageTopRepos);

            } catch (err) {
                setError(`Ëé∑Âèñ‰ª™Ë°®ÁõòÊï∞ÊçÆÂ§±Ë¥•: ${err.message}`);
                console.error(err);
            } finally {
                setLoadingLanguages(false);
            }
        };

        fetchDashboardData();
    }, []);

    const totalCount = languageData.reduce((sum, item) => sum + item.count, 0);
    const maxCount = Math.max(...languageData.map(item => item.count));

    return (
        <div className="dashboard">
            <h1 className="page-title">Êï∞ÊçÆÊ¶ÇËßà</h1>

            <div className="stats-overview">
                <div className="card stat-card">
                    <div className="stat-value">{loadingStats ? '...' : stats.totalRepositories.toLocaleString()}</div>
                    <div className="stat-label">‰ªìÂ∫ìÊÄªÊï∞</div>
                </div>
                <div className="card stat-card">
                    <div className="stat-value">{loadingStats ? '...' : stats.totalStars.toLocaleString()}</div>
                    <div className="stat-label">ÊÄªÊòüÊ†áÊï∞</div>
                </div>
                <div className="card stat-card">
                    <div className="stat-value">{loadingStats ? '...' : stats.totalContributors.toLocaleString()}</div>
                    <div className="stat-label">Ë¥°ÁåÆËÄÖÊÄªÊï∞</div>
                </div>
            </div>

            {error && <div className="error-message">ÈîôËØØ: {error}</div>}

            <div className="dashboard-row">
                <div className="card language-distribution-card">
                    <div className="card-header">
                        <h2 className="card-title">ÁºñÁ®ãËØ≠Ë®ÄÂàÜÂ∏É</h2>
                    </div>
                    <div className="card-body">
                        {loadingLanguages ? (
                            <div className="loading-container">Ê≠£Âú®Âä†ËΩΩËØ≠Ë®ÄÊï∞ÊçÆ...</div>
                        ) : !languageData || languageData.length === 0 ? (
                            <div className="empty-data">Ê≤°ÊúâÊâæÂà∞ËØ≠Ë®ÄÂàÜÂ∏ÉÊï∞ÊçÆ</div>
                        ) : (
                            <div className="horizontal-bars-container">
                                {languageData.slice(0, 10).map((item, index) => {
                                    const percentage = totalCount > 0 ? (item.count / totalCount * 100).toFixed(1) : 0;
                                    const barWidth = maxCount > 0 ? (item.count / maxCount * 100).toFixed(1) + '%' : '0%';

                                    return (
                                        <div
                                            key={index}
                                            className={`language-bar-item ${hoveredLanguage === item.language ? 'hovered' : ''}`}
                                            onMouseEnter={() => setHoveredLanguage(item.language)}
                                            onMouseLeave={() => setHoveredLanguage(null)}
                                        >
                                            <div className="language-name">{item.language}</div>
                                            <div className="bar-container">
                                                <div
                                                    className="bar-fill"
                                                    style={{
                                                        width: barWidth,
                                                        //height: '20px',
                                                        backgroundColor: getLanguageColor(item.language),
                                                    }}
                                                />
                                            </div>
                                            <div className="language-count">
                                                {item.count.toLocaleString()} ({percentage}%)
                                            </div>

                                            {hoveredLanguage === item.language && topRepos[item.language] && (
                                                <div className="language-top-repos">
                                                    <h4>{item.language} ÁÉ≠Èó®‰ªìÂ∫ì</h4>
                                                    <ul>
                                                        {topRepos[item.language].map((repo, i) => (
                                                            <li key={i}>
                                                                <div className="repo-name">{repo.repo_name}</div>
                                                                <div className="repo-stats">
                                                                    <span>‚≠ê {repo.stars.toLocaleString()}</span>
                                                                    <span>üî± {repo.forks.toLocaleString()}</span>
                                                                </div>
                                                            </li>
                                                        ))}
                                                    </ul>
                                                </div>
                                            )}
                                        </div>
                                    );
                                })}
                            </div>
                        )}
                    </div>
                </div>

                <div className="card top-repositories-card">
                    <div className="card-header">
                        <h2 className="card-title">ÁÉ≠Èó®‰ªìÂ∫ì</h2>
                    </div>
                    <div className="card-body">
                        <ul className="repo-list">
                            {[
                                { name: 'freeCodeCamp/freeCodeCamp', stars: 380000, description: 'The freeCodeCamp.org open-source codebase and curriculum.' },
                                { name: 'vuejs/vue', stars: 210000, description: 'üññ Vue.js is a progressive, incrementally-adoptable JavaScript framework for building UI on the web.' },
                                { name: 'facebook/react', stars: 200000, description: 'A declarative, efficient, and flexible JavaScript library for building user interfaces.' }
                            ].map(repo => (
                                <li key={repo.name} className="repo-item">
                                    <div className="repo-header">
                                        <h3 className="repo-name">{repo.name}</h3>
                                        <div className="repo-stars">
                                            ‚≠ê {repo.stars.toLocaleString()}
                                        </div>
                                    </div>
                                    <p className="repo-description">{repo.description}</p>
                                </li>
                            ))}
                        </ul>
                        <p style={{ fontSize: '0.8em', color: 'var(--light-text-tertiary)', marginTop: '10px' }}>Ê≥®ÔºöÊ≠§ÁÉ≠Èó®‰ªìÂ∫ìÂàóË°®‰∏∫ÈùôÊÄÅÁ§∫‰æãÊï∞ÊçÆ„ÄÇ</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
