import { useState } from 'react';

const mockData = {
    repositories: [
        { name: 'react', stars: 182000, issues: 743, openIssues: 512, closedIssues: 8462, owner: 'facebook' },
        { name: 'vue', stars: 153000, issues: 518, openIssues: 347, closedIssues: 6231, owner: 'vuejs' },
        { name: 'tensorflow', stars: 147000, issues: 912, openIssues: 682, closedIssues: 9138, owner: 'tensorflow' },
        { name: 'flutter', stars: 139000, issues: 873, openIssues: 597, closedIssues: 7621, owner: 'flutter' },
        { name: 'vscode', stars: 132000, issues: 1287, openIssues: 854, closedIssues: 12458, owner: 'microsoft' },
        { name: 'kubernetes', stars: 126000, issues: 1532, openIssues: 1043, closedIssues: 15683, owner: 'kubernetes' },
        { name: 'linux', stars: 118000, issues: 894, openIssues: 632, closedIssues: 8743, owner: 'torvalds' },
        { name: 'django', stars: 62000, issues: 421, openIssues: 289, closedIssues: 4832, owner: 'django' },
        { name: 'ruby-on-rails', stars: 51000, issues: 387, openIssues: 256, closedIssues: 4512, owner: 'rails' },
        { name: 'spring-framework', stars: 47000, issues: 342, openIssues: 217, closedIssues: 4123, owner: 'spring-projects' }
    ]
};

const StarsIssuesRelation = () => {
    const [data] = useState(mockData);
    const [sortBy, setSortBy] = useState('stars');
    const [sortOrder, setSortOrder] = useState('desc');

    // 排序函数
    const sortRepositories = (repos) => {
        return [...repos].sort((a, b) => {
            const multiplier = sortOrder === 'asc' ? 1 : -1;
            return multiplier * (a[sortBy] - b[sortBy]);
        });
    };

    // 处理排序点击
    const handleSort = (field) => {
        if (field === sortBy) {
            setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
        } else {
            setSortBy(field);
            setSortOrder('desc');
        }
    };

    // 格式化数字
    const formatNumber = (number) => {
        return number.toLocaleString();
    };

    // 计算星标和问题的比率
    const calculateRatio = (stars, issues) => {
        return (stars / issues).toFixed(0);
    };

    // 获取关闭率百分比
    const getClosedPercentage = (closed, total) => {
        return ((closed / (closed + total)) * 100).toFixed(1);
    };

    return (
        <div className="stars-issues-page">
            <h1 className="page-title">星标与问题关系</h1>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">星标与问题散点图</h2>
                </div>
                <div className="card-body">
                    <div className="scatter-plot-container">
                        {/* 实际项目中可使用Recharts等图表库创建散点图 */}
                        <div className="mock-scatter-plot">
                            {data.repositories.map(repo => (
                                <div
                                    key={repo.name}
                                    className="scatter-point"
                                    style={{
                                        left: `${(repo.stars / 200000) * 100}%`,
                                        bottom: `${(repo.issues / 2000) * 100}%`,
                                        backgroundColor: '#3b82f6',
                                        width: Math.log(repo.stars) / 2,
                                        height: Math.log(repo.stars) / 2,
                                        opacity: 0.7
                                    }}
                                    title={`${repo.owner}/${repo.name}: ${repo.stars.toLocaleString()} 星标, ${repo.issues} 问题`}
                                />
                            ))}
                            <div className="x-axis">
                                <span className="axis-label">星标数量</span>
                            </div>
                            <div className="y-axis">
                                <span className="axis-label">问题数量</span>
                            </div>
                        </div>
                    </div>
                    <div className="scatter-plot-description">
                        <p>每个点代表一个仓库，X轴表示星标数量，Y轴表示问题数量。点的大小与仓库的星标数量成正比。</p>
                    </div>
                </div>
            </div>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">仓库星标与问题统计</h2>
                </div>
                <div className="card-body">
                    <div className="repositories-table">
                        <table>
                            <thead>
                                <tr>
                                    <th>仓库</th>
                                    <th
                                        className={`sortable ${sortBy === 'stars' ? 'active' : ''}`}
                                        onClick={() => handleSort('stars')}
                                    >
                                        星标 {sortBy === 'stars' && (sortOrder === 'asc' ? '↑' : '↓')}
                                    </th>
                                    <th
                                        className={`sortable ${sortBy === 'issues' ? 'active' : ''}`}
                                        onClick={() => handleSort('issues')}
                                    >
                                        总问题数 {sortBy === 'issues' && (sortOrder === 'asc' ? '↑' : '↓')}
                                    </th>
                                    <th
                                        className={`sortable ${sortBy === 'openIssues' ? 'active' : ''}`}
                                        onClick={() => handleSort('openIssues')}
                                    >
                                        未解决 {sortBy === 'openIssues' && (sortOrder === 'asc' ? '↑' : '↓')}
                                    </th>
                                    <th>解决率</th>
                                    <th>星/问题比率</th>
                                </tr>
                            </thead>
                            <tbody>
                                {sortRepositories(data.repositories).map(repo => (
                                    <tr key={repo.name}>
                                        <td className="repo-name">{repo.owner}/{repo.name}</td>
                                        <td>{formatNumber(repo.stars)}</td>
                                        <td>{formatNumber(repo.issues)}</td>
                                        <td>{formatNumber(repo.openIssues)}</td>
                                        <td className="closed-rate">
                                            <div className="progress-bar">
                                                <div
                                                    className="progress-fill"
                                                    style={{ width: `${getClosedPercentage(repo.closedIssues, repo.openIssues)}%` }}
                                                ></div>
                                            </div>
                                            <span>{getClosedPercentage(repo.closedIssues, repo.openIssues)}%</span>
                                        </td>
                                        <td>{calculateRatio(repo.stars, repo.issues)}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">洞察分析</h2>
                </div>
                <div className="card-body">
                    <div className="insights">
                        <div className="insight-item">
                            <h3 className="insight-title">星标与问题数量关系</h3>
                            <p className="insight-description">
                                从数据中可以看出，一般情况下，星标数越多的项目，问题数量也会随之增加，
                                说明用户参与度与项目活跃度呈正相关关系。
                            </p>
                        </div>
                        <div className="insight-item">
                            <h3 className="insight-title">解决效率</h3>
                            <p className="insight-description">
                                {data.repositories.sort((a, b) => {
                                    const aRate = a.closedIssues / (a.closedIssues + a.openIssues);
                                    const bRate = b.closedIssues / (b.closedIssues + b.openIssues);
                                    return bRate - aRate;
                                })[0].name} 项目在问题解决效率方面表现最佳，
                                这可能与其维护团队的规模和项目的成熟度有关。
                            </p>
                        </div>
                        <div className="insight-item">
                            <h3 className="insight-title">星标与维护质量</h3>
                            <p className="insight-description">
                                星标数量与问题解决率之间并不总是呈正比关系，一些较小但更专注的项目
                                可能在维护质量方面表现更好。
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default StarsIssuesRelation;
