import { useState, useEffect } from 'react';
import API from '../config/api';

const ContributorLocations = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [hoveredLocation, setHoveredLocation] = useState(null);
  const [selectedView, setSelectedView] = useState('chart'); // 'chart' or 'map'

  useEffect(() => {
    async function fetchData() {
      try {
        setLoading(true);
        const response = await fetch(API.contributorLocations);
        if (!response.ok) throw new Error('获取数据失败');
        const result = await response.json();
        setData(result);
      } catch (err) {
        setError(err.message);
        console.error('获取贡献者地区分布出错:', err);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, []);

  if (loading) return <div className="loading-indicator">数据加载中...</div>;
  if (error) return <div className="error-message">加载出错: {error}</div>;
  if (!data || !Array.isArray(data) || data.length === 0) {
    return <div className="empty-data">没有贡献者地区分布数据</div>;
  }

  // 计算所需的统计数据
  const totalLocatedContributors = data.reduce((sum, location) => sum + (location.contributor_count || 0), 0);

  // 转换数据格式，添加百分比
  const locations = data.map((location, index) => ({
    name: location.location,
    count: location.contributor_count || 0,
    percentage: ((location.contributor_count || 0) / totalLocatedContributors * 100).toFixed(1),
    rank: index + 1
  }));

  // 生成渐变色
  const getLocationColor = (index, total) => {
    const hue = 200; // 蓝色调
    const saturation = 70;
    const minLightness = 40;
    const maxLightness = 75;

    // 根据索引计算亮度值，使颜色由深到浅
    const lightness = maxLightness - ((index / Math.max(1, (total - 1))) * (maxLightness - minLightness));

    return `hsl(${hue}, ${saturation}%, ${lightness}%)`;
  };

  // 生成随机坐标用于地图
  const getRandomCoordinates = (seed) => {
    // 使用简单的伪随机生成器来确保相同的seed总是生成相同的结果
    const x = Math.abs(Math.sin(seed * 9999)) * 80 + 10; // 10% to 90% of container width
    const y = Math.abs(Math.cos(seed * 7777)) * 70 + 15; // 15% to 85% of container height
    return { x: `${x}%`, y: `${y}%` };
  };

  return (
    <div className="contributor-locations-page">
      <h1 className="page-title">贡献者地区分布</h1>

      <div className="stats-overview">
        <div className="card stat-card">
          <div className="stat-value">{totalLocatedContributors.toLocaleString()}</div>
          <div className="stat-label">有位置信息的贡献者</div>
        </div>
        <div className="card stat-card">
          <div className="stat-value">{locations.length}</div>
          <div className="stat-label">主要国家/地区</div>
        </div>
        {locations.length > 0 && (
          <div className="card stat-card">
            <div className="stat-value">{locations[0].name}</div>
            <div className="stat-label">贡献最多地区</div>
            <div className="stat-description">{locations[0].count.toLocaleString()} 名贡献者</div>
          </div>
        )}
      </div>

      <div className="card">
        <div className="card-header">
          <h2 className="card-title">地区分布图</h2>
          <div className="view-toggle">
            <button 
              className={`toggle-button ${selectedView === 'chart' ? 'active' : ''}`}
              onClick={() => setSelectedView('chart')}
            >
              柱状图
            </button>
            <button 
              className={`toggle-button ${selectedView === 'map' ? 'active' : ''}`}
              onClick={() => setSelectedView('map')}
            >
              地图视图
            </button>
          </div>
        </div>
        <div className="card-body">
          {selectedView === 'chart' ? (
            <div className="locations-chart">
              {locations.map((location, index) => (
                <div 
                  key={location.name} 
                  className={`location-bar ${hoveredLocation === index ? 'hovered' : ''}`}
                  onMouseEnter={() => setHoveredLocation(index)}
                  onMouseLeave={() => setHoveredLocation(null)}
                >
                  <div className="location-rank">{location.rank}</div>
                  <div className="location-name">{location.name}</div>
                  <div className="bar-container">
                    <div
                      className="bar-fill"
                      style={{
                        width: `${location.percentage}%`,
                        backgroundColor: getLocationColor(index, locations.length)
                      }}
                    ></div>
                    <span className="bar-value">
                      {location.count.toLocaleString()} ({location.percentage}%)
                    </span>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="map-container">
              <div className="map-visualization">
                {locations.slice(0, 20).map((location, index) => {
                  const size = Math.max(30, Math.min(80, location.count / totalLocatedContributors * 500));
                  const coords = getRandomCoordinates(index);
                  return (
                    <div
                      key={location.name}
                      className={`map-bubble ${hoveredLocation === index ? 'hovered' : ''}`}
                      style={{
                        width: `${size}px`,
                        height: `${size}px`,
                        left: coords.x,
                        top: coords.y,
                        backgroundColor: getLocationColor(index, locations.length),
                        opacity: hoveredLocation === index ? 1 : 0.7
                      }}
                      onMouseEnter={() => setHoveredLocation(index)}
                      onMouseLeave={() => setHoveredLocation(null)}
                    >
                      <div className="bubble-content">
                        <div className="bubble-name">{location.name}</div>
                        <div className="bubble-count">{location.count.toLocaleString()}</div>
                      </div>
                      {hoveredLocation === index && (
                        <div className="bubble-tooltip">
                          <div className="tooltip-rank">排名 #{location.rank}</div>
                          <div className="tooltip-name">{location.name}</div>
                          <div className="tooltip-count">{location.count.toLocaleString()} 名贡献者</div>
                          <div className="tooltip-percentage">{location.percentage}% 的总贡献</div>
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
              <div className="map-legend">
                <div className="legend-title">前20个地区 (气泡大小代表贡献者数量)</div>
                <div className="legend-colors">
                  {[0, 4, 9, 14, 19].map(index => (
                    <div key={index} className="legend-item">
                      <div
                        className="legend-color"
                        style={{ backgroundColor: getLocationColor(index, 20) }}
                      ></div>
                      <div className="legend-label">
                        {index === 0 ? '排名靠前' : index === 19 ? '排名靠后' : ''}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      <div className="card">
        <div className="card-header">
          <h2 className="card-title">贡献者地区洞察</h2>
        </div>
        <div className="card-body">
          <div className="locations-insights">
            <div className="insight-item">
              <h3 className="insight-title">地区多样性</h3>
              <p>GitHub开源社区展现了全球化的特点，贡献者来自世界各地。前10名地区占总贡献者的
                {(locations.slice(0, 10).reduce((sum, loc) => sum + parseFloat(loc.percentage), 0)).toFixed(1)}%。</p>
            </div>
            <div className="insight-item">
              <h3 className="insight-title">主要贡献地区</h3>
              <p>排名前三的地区分别是 {locations.slice(0, 3).map(l => l.name).join('、')}，
                共有 {locations.slice(0, 3).reduce((sum, l) => sum + l.count, 0).toLocaleString()} 名贡献者，
                这反映了这些地区在开源社区中的活跃程度。</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContributorLocations;
