import { MenuIcon, RefreshIcon } from '@heroicons/react/outline';
import { useTheme } from '../../contexts/ThemeContext';
import { useGitHubData } from '../../contexts/GitHubDataContext';
import toast from 'react-hot-toast';

const Header = ({ toggleSidebar }) => {
    const { theme } = useTheme();
    const { refreshData, lastUpdated, isLoading } = useGitHubData();

    const handleRefresh = async () => {
        try {
            await refreshData();
            toast.success('数据已更新');
        } catch (error) {
            toast.error('更新数据失败');
        }
    };

    const formattedDate = lastUpdated
        ? new Date(lastUpdated).toLocaleString('zh-CN', {
            year: 'numeric',
            month: 'long',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit'
        })
        : '加载中...';

    return (
        <header className={`app-header ${theme}`}>
            <div className="header-left">
                <button
                    className="sidebar-toggle"
                    onClick={toggleSidebar}
                    aria-label="Toggle sidebar"
                >
                    <MenuIcon className="w-6 h-6" />
                </button>
                <h1 className="site-title">GitHub热门仓库分析</h1>
            </div>
            <div className="header-right">
                <div className="last-updated">
                    <span className="label">最后更新: </span>
                    <span className="value">{formattedDate}</span>
                </div>
                <button
                    className="refresh-btn"
                    onClick={handleRefresh}
                    disabled={isLoading}
                    aria-label="Refresh data"
                >
                    <RefreshIcon className={`w-5 h-5 ${isLoading ? 'animate-spin' : ''}`} />
                    <span>{isLoading ? '更新中...' : '刷新数据'}</span>
                </button>
            </div>
        </header>
    );
};

export default Header;
