import { useState } from 'react';

const mockData = {
    repositories: [
        { name: 'react', stars: 182000, contributors: 1423, language: 'JavaScript' },
        { name: 'vue', stars: 153000, contributors: 376, language: 'JavaScript' },
        { name: 'tensorflow', stars: 147000, contributors: 2541, language: 'Python' },
        { name: 'flutter', stars: 139000, contributors: 854, language: 'Dart' },
        { name: 'vscode', stars: 132000, contributors: 1287, language: 'TypeScript' },
        { name: 'kubernetes', stars: 126000, contributors: 3542, language: 'Go' },
        { name: 'linux', stars: 118000, contributors: 5421, language: 'C' },
        { name: 'django', stars: 62000, contributors: 1243, language: 'Python' },
        { name: 'ruby-on-rails', stars: 51000, contributors: 3876, language: 'Ruby' },
        { name: 'spring-framework', stars: 47000, contributors: 426, language: 'Java' }
    ]
};

// 根据语言名称生成颜色
const getLanguageColor = (language) => {
    const colorMap = {
        JavaScript: '#f1e05a',
        TypeScript: '#2b7489',
        Python: '#3572A5',
        Java: '#b07219',
        Go: '#00ADD8',
        'C++': '#f34b7d',
        Ruby: '#701516',
        C: '#555555',
        Dart: '#00B4AB'
    };

    return colorMap[language] || '#999';
};

const StarsContributorsRelation = () => {
    const [data] = useState(mockData);

    return (
        <div className="stars-contributors-page">
            <h1 className="page-title">星标与贡献者关系</h1>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">星标/贡献者散点图</h2>
                </div>
                <div className="card-body">
                    <div className="scatter-plot-container">
                        {/* 实际项目中这里会使用Recharts等库绘制散点图 */}
                        <div className="mock-scatter-plot">
                            {data.repositories.map(repo => (
                                <div
                                    key={repo.name}
                                    className="scatter-point"
                                    style={{
                                        left: `${(repo.stars / 200000) * 100}%`,
                                        bottom: `${(repo.contributors / 6000) * 100}%`,
                                        backgroundColor: getLanguageColor(repo.language),
                                        width: Math.log(repo.stars) / 2,
                                        height: Math.log(repo.stars) / 2
                                    }}
                                    title={`${repo.name}: ${repo.stars.toLocaleString()} 星标, ${repo.contributors.toLocaleString()} 贡献者`}
                                />
                            ))}
                            <div className="x-axis">
                                <span className="axis-label">星标数量</span>
                            </div>
                            <div className="y-axis">
                                <span className="axis-label">贡献者数量</span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">项目详情</h2>
                </div>
                <div className="card-body">
                    <div className="repositories-table">
                        <table>
                            <thead>
                                <tr>
                                    <th>项目名称</th>
                                    <th>星标数</th>
                                    <th>贡献者数</th>
                                    <th>星标/贡献者比率</th>
                                    <th>编程语言</th>
                                </tr>
                            </thead>
                            <tbody>
                                {data.repositories.map(repo => (
                                    <tr key={repo.name}>
                                        <td className="project-name">{repo.name}</td>
                                        <td>{repo.stars.toLocaleString()}</td>
                                        <td>{repo.contributors.toLocaleString()}</td>
                                        <td>{(repo.stars / repo.contributors).toFixed(1)}</td>
                                        <td>
                                            <span
                                                className="language-indicator"
                                                style={{ backgroundColor: getLanguageColor(repo.language) }}
                                            ></span>
                                            {repo.language}
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default StarsContributorsRelation;
