import { useState, useEffect } from 'react';
import API from '../config/api';

const ActivityHeatmap = () => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        async function fetchData() {
            try {
                setLoading(true);
                const response = await fetch(API.activityHeatmap);
                if (!response.ok) throw new Error('获取数据失败');
                const result = await response.json();
                setData(result);
            } catch (err) {
                setError(err.message);
                console.error('获取活动热图数据出错:', err);
            } finally {
                setLoading(false);
            }
        }

        fetchData();
    }, []);

    if (loading) return <div className="loading-indicator">数据加载中...</div>;
    if (error) return <div className="error-message">加载出错: {error}</div>;
    if (!data || !Array.isArray(data) || data.length === 0) {
        return <div className="empty-data">没有活动热图数据</div>;
    }

    // 处理数据，确保每个日期项都有完整的信息
    const processedDays = data.map(item => ({
        date: item.activity_date,
        count: item.commit_count || 0,
        weekday: new Date(item.activity_date).getDay()
    }));

    // 计算统计信息
    const totalContributions = processedDays.reduce((sum, day) => sum + day.count, 0);
    const averageDailyContributions = Math.round(totalContributions / processedDays.length);
    const mostActiveDay = processedDays.reduce(
        (max, day) => (day.count > max.count ? day : max),
        { date: '', count: 0 }
    );

    // 根据贡献数计算热图单元格的颜色
    const getHeatmapColor = (count) => {
        if (count === 0) return '#ebedf0';
        if (count < 5) return '#9be9a8';
        if (count < 10) return '#40c463';
        if (count < 20) return '#30a14e';
        return '#216e39';
    };

    // 将日期格式化为可读形式
    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleDateString('zh-CN', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
        });
    };

    // 计算每月的位置，用于显示月份标签
    const getMonthLabels = () => {
        const months = [];
        let currentMonth = -1;

        processedDays.forEach((day, index) => {
            const month = new Date(day.date).getMonth();
            if (month !== currentMonth) {
                months.push({
                    name: new Date(day.date).toLocaleDateString('zh-CN', { month: 'short' }),
                    index
                });
                currentMonth = month;
            }
        });

        return months;
    };

    // 计算热图的行列数
    const getGridDimensions = () => {
        const weeks = Math.ceil(processedDays.length / 7);
        return {
            rows: 7,
            columns: weeks
        };
    };

    // 计算每周的贡献
    const getWeeksData = () => {
        const weeks = [];
        const sortedDays = [...processedDays].sort((a, b) => new Date(a.date) - new Date(b.date));

        for (let i = 0; i < sortedDays.length; i += 7) {
            const weekDays = sortedDays.slice(i, i + 7);
            if (weekDays.length > 0) {
                const weekTotal = weekDays.reduce((sum, day) => sum + day.count, 0);
                weeks.push({
                    startDate: weekDays[0].date,
                    endDate: weekDays[weekDays.length - 1].date,
                    total: weekTotal
                });
            }
        }

        return weeks;
    };

    const { rows, columns } = getGridDimensions();
    const weeks = getWeeksData();

    return (
        <div className="activity-heatmap-page">
            <h1 className="page-title">活跃度热图</h1>

            <div className="stats-overview">
                <div className="card stat-card">
                    <div className="stat-value">{totalContributions.toLocaleString()}</div>
                    <div className="stat-label">总贡献数</div>
                </div>
                <div className="card stat-card">
                    <div className="stat-value">{averageDailyContributions}</div>
                    <div className="stat-label">日均贡献</div>
                </div>
                {mostActiveDay && mostActiveDay.date && (
                    <div className="card stat-card">
                        <div className="stat-value">{formatDate(mostActiveDay.date)}</div>
                        <div className="stat-label">最活跃的一天</div>
                        <div className="stat-description">{mostActiveDay.count} 次贡献</div>
                    </div>
                )}
            </div>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">年度贡献热图</h2>
                </div>
                <div className="card-body">
                    <div className="heatmap-container">
                        <div className="weekday-labels">
                            <div className="weekday-label">周日</div>
                            <div className="weekday-label">周一</div>
                            <div className="weekday-label">周二</div>
                            <div className="weekday-label">周三</div>
                            <div className="weekday-label">周四</div>
                            <div className="weekday-label">周五</div>
                            <div className="weekday-label">周六</div>
                        </div>

                        <div
                            className="heatmap-grid"
                            style={{
                                gridTemplateColumns: `repeat(${columns}, 12px)`,
                                gridTemplateRows: `repeat(${rows}, 12px)`
                            }}
                        >
                            {processedDays.map((day, index) => (
                                <div
                                    key={day.date}
                                    className="heatmap-cell"
                                    style={{
                                        backgroundColor: getHeatmapColor(day.count),
                                        gridRow: day.weekday + 1,
                                        gridColumn: Math.floor(index / 7) + 1
                                    }}
                                    title={`${formatDate(day.date)}: ${day.count} 次贡献`}
                                />
                            ))}
                        </div>
                    </div>

                    <div className="heatmap-legend">
                        <div className="legend-label">较少</div>
                        {[0, 4, 9, 19, 30].map(value => (
                            <div
                                key={value}
                                className="legend-cell"
                                style={{ backgroundColor: getHeatmapColor(value) }}
                            />
                        ))}
                        <div className="legend-label">较多</div>
                    </div>
                </div>
            </div>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">最近活动</h2>
                </div>
                <div className="card-body">
                    <div className="recent-activity">
                        <div className="activity-chart">
                            {weeks.slice(-10).map(week => (
                                <div key={week.startDate} className="week-bar">
                                    <div className="week-date">
                                        {formatDate(week.startDate)}
                                    </div>
                                    <div className="bar-container">
                                        <div
                                            className="bar-fill"
                                            style={{
                                                width: `${Math.min((week.total / 150) * 100, 100)}%`,
                                                backgroundColor: '#3b82f6'
                                            }}
                                        ></div>
                                        <span className="bar-value">
                                            {week.total}
                                        </span>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ActivityHeatmap;
