import React, { useState, useEffect } from 'react';
import API from '../config/api';

// Ê†πÊçÆËØ≠Ë®ÄÂêçÁß∞ÁîüÊàêÈ¢úËâ≤
const getLanguageColor = (language) => {
    const colorMap = {
        JavaScript: '#f1e05a',
        TypeScript: '#2b7489',
        Python: '#3572A5',
        Java: '#b07219',
        Go: '#00ADD8',
        'C++': '#f34b7d',
        Ruby: '#701516',
        PHP: '#4F5D95',
        Rust: '#dea584',
        'C#': '#178600',
        Swift: '#ffac45',
        Kotlin: '#F18E33',
        'C': '#555555',         // Á§∫‰æãÔºöÊ∑ªÂä† C ËØ≠Ë®Ä
        'Shell': '#89e051',     // Á§∫‰æãÔºöÊ∑ªÂä† Shell
        'HTML': '#e34c26',      // Á§∫‰æãÔºöÊ∑ªÂä† HTML
        'CSS': '#563d7c',       // Á§∫‰æãÔºöÊ∑ªÂä† CSS
    };

    // Â¶ÇÊûúÈ¢ÑÂÆö‰πâ‰∫ÜÈ¢úËâ≤ÔºåÁõ¥Êé•ËøîÂõû
    if (colorMap[language]) {
        return colorMap[language];
    }

    // Â§áÁî®È¢úËâ≤ÔºöÂü∫‰∫éËØ≠Ë®ÄÂêçÁß∞ÁîüÊàê HSL È¢úËâ≤ÔºåË∞ÉÊï¥È•±ÂíåÂ∫¶Âíå‰∫ÆÂ∫¶
    // Â∞ÜÈ•±ÂíåÂ∫¶‰ªé 70% Ë∞ÉÊï¥‰∏∫ 65%Ôºå‰∫ÆÂ∫¶‰ªé 60% Ë∞ÉÊï¥‰∏∫ 65%
    const hue = Math.abs(language.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0)) % 360;
    return `hsl(${hue}, 65%, 65%)`;
};

const LanguageDistribution = () => {
    const [data, setData] = useState([]);
    const [topRepos, setTopRepos] = useState({});
    const [hoveredLanguage, setHoveredLanguage] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                const response = await fetch(API.languageDistribution);
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                const result = await response.json();
                setData(result);

                // ‰∏∫ÊØèÁßçËØ≠Ë®ÄËé∑ÂèñÂâç6‰∏™ÊòüÊ†áÊúÄÈ´òÁöÑ‰ªìÂ∫ì
                const languageTopRepos = {};
                for (const lang of result.slice(0, 15)) {/* ÈÅçÂéÜÂâç10ÁßçËØ≠Ë®Ä */
                    const reposResponse = await fetch(`${API.languageTopRepos}?language=${encodeURIComponent(lang.language)}`);
                    if (reposResponse.ok) {
                        const reposData = await reposResponse.json();
                        languageTopRepos[lang.language] = reposData;
                    }
                }
                setTopRepos(languageTopRepos);
            } catch (err) {
                setError(`Ëé∑ÂèñÊï∞ÊçÆÂ§±Ë¥•: ${err.message}`);
                console.error(err);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    // ÊòæÁ§∫Âä†ËΩΩÁä∂ÊÄÅ
    if (loading) {
        return <div className="loading-container">
            Ê≠£Âú®Âä†ËΩΩËØ≠Ë®ÄÂàÜÂ∏ÉÊï∞ÊçÆ...</div>;
    }

    // ÊòæÁ§∫ÈîôËØØ
    if (error) {
        return <div className="error-container">ÈîôËØØ: {error}</div>;
    }

    // Á°Æ‰øùÊï∞ÊçÆÂ≠òÂú®‰∏îÈùûÁ©∫
    if (!data || data.length === 0) {
        return <div className="empty-data">Ê≤°ÊúâÊâæÂà∞ËØ≠Ë®ÄÂàÜÂ∏ÉÊï∞ÊçÆ</div>;
    }

    // ËÆ°ÁÆóÊÄªÊï∞ÂíåÊúÄÂ§ßÂÄºÔºåÁî®‰∫éÁôæÂàÜÊØîËÆ°ÁÆó
    const totalCount = data.reduce((sum, item) => sum + item.count, 0);
    const maxCount = Math.max(...data.map(item => item.count));

    return (
        <div className="language-distribution-container">
            <h2>ÁºñÁ®ãËØ≠Ë®ÄÂàÜÂ∏É</h2>
            <div className="horizontal-bars-container">
                {data.map((item, index) => {
                    const percentage = (item.count / totalCount * 100).toFixed(1);//ËÆ°ÁÆóÁôæÂàÜÊØî
                    const barWidth = (item.count / maxCount * 100).toFixed(1) + '%';//ËÆ°ÁÆóÂÆΩÂ∫¶
                    //const barWidth = (item.count / totalCount * 100).toFixed(1) + '%';

                    return (
                        <div
                            key={index}
                            className={`language-bar-item ${hoveredLanguage === item.language ? 'hovered' : ''}`}//hoveredLanguage ÊòØÊÇ¨ÂÅúÁöÑËØ≠Ë®Ä
                            onMouseEnter={() => setHoveredLanguage(item.language)}//setHoveredLanguage ÊòØËÆæÁΩÆÊÇ¨ÂÅúÁöÑËØ≠Ë®Ä
                            onMouseLeave={() => setHoveredLanguage(null)}//setHoveredLanguage ÊòØËÆæÁΩÆÊÇ¨ÂÅúÁöÑËØ≠Ë®Ä
                        >
                            <div className="language-name">{item.language}</div>
                            <div className="bar-container">
                                <div
                                    style={{//style ÊòØcssÊ†∑Âºè
                                        width: barWidth,//barWidth ÊòØÁôæÂàÜÊØî
                                        backgroundColor: getLanguageColor(item.language),//Ê†πÊçÆËØ≠Ë®ÄÂêçÁß∞ÁîüÊàêÈ¢úËâ≤
                                        //backgroundColor: 'red',
                                        //height: '100%',
                                        height: '20px',
                                        borderRadius: '4px',//borderRadius ÊòØÂúÜËßí
                                        display: 'block',//display ÊòØÊòæÁ§∫ÊñπÂºè
                                        opacity: 0.8//opacity ÊòØÈÄèÊòéÂ∫¶
                                    }}
                                />
                            </div>
                            <div className="language-count">
                                {item.count.toLocaleString()} ({percentage}%)
                            </div>

                            {/* ÊÇ¨ÂÅúÊó∂ÊòæÁ§∫ËØ•ËØ≠Ë®ÄÁöÑÁÉ≠Èó®‰ªìÂ∫ì */}
                            {hoveredLanguage === item.language && topRepos[item.language] && (
                                <div className="language-top-repos">
                                    <h4>{item.language} ÁÉ≠Èó®‰ªìÂ∫ì</h4>
                                    <ul>
                                        {topRepos[item.language].map((repo, i) => ( /* ÈÅçÂéÜÁÉ≠Èó®‰ªìÂ∫ì */
                                            <li key={i}>{/* ÊØè‰∏™ÁÉ≠Èó®‰ªìÂ∫ìÁöÑÂîØ‰∏ÄÊ†áËØÜ */}
                                                <div className="repo-name">{repo.repo_name}</div>
                                                <div className="repo-stats">
                                                    <span>‚≠ê {repo.stars.toLocaleString()}</span>
                                                    <span>üî± {repo.forks.toLocaleString()}</span>
                                                </div>
                                            </li>
                                        ))}
                                    </ul>
                                </div>
                            )}
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

export default LanguageDistribution;
