import { useState } from 'react';

const mockData = {
    repositories: [
        {
            name: 'react',
            description: '用于构建用户界面的JavaScript库',
            stars: 182000,
            forks: 35800,
            language: 'JavaScript',
            owner: 'facebook'
        },
        {
            name: 'vue',
            description: '渐进式JavaScript框架',
            stars: 153000,
            forks: 29500,
            language: 'JavaScript',
            owner: 'vuejs'
        },
        {
            name: 'tensorflow',
            description: '机器学习框架',
            stars: 147000,
            forks: 83600,
            language: 'Python',
            owner: 'tensorflow'
        },
        {
            name: 'flutter',
            description: 'Google的UI工具包，用于从单一代码库构建移动、Web和桌面应用',
            stars: 139000,
            forks: 21700,
            language: 'Dart',
            owner: 'flutter'
        },
        {
            name: 'vscode',
            description: 'Visual Studio Code',
            stars: 132000,
            forks: 23800,
            language: 'TypeScript',
            owner: 'microsoft'
        },
        {
            name: 'kubernetes',
            description: '容器编排系统',
            stars: 126000,
            forks: 45900,
            language: 'Go',
            owner: 'kubernetes'
        }
    ]
};

// 根据语言名称生成颜色
const getLanguageColor = (language) => {
    const colorMap = {
        JavaScript: '#f1e05a',
        TypeScript: '#2b7489',
        Python: '#3572A5',
        Java: '#b07219',
        Go: '#00ADD8',
        'C++': '#f34b7d',
        Ruby: '#701516',
        Dart: '#00B4AB'
    };

    return colorMap[language] || '#999';
};

const TopRepositories = () => {
    const [data] = useState(mockData);

    return (
        <div className="top-repositories-page">
            <h1 className="page-title">最受欢迎仓库</h1>

            <div className="card">
                <div className="card-header">
                    <h2 className="card-title">按星标排名</h2>
                </div>
                <div className="card-body">
                    <div className="repositories-list">
                        {data.repositories.map((repo, index) => (
                            <div key={repo.name} className="repository-item">
                                <div className="repository-rank">{index + 1}</div>
                                <div className="repository-info">
                                    <div className="repository-header">
                                        <h3 className="repository-name">
                                            <span className="repository-owner">{repo.owner}/</span>
                                            {repo.name}
                                        </h3>
                                        <div className="repository-stats">
                                            <div className="repository-stars">
                                                ⭐ {repo.stars.toLocaleString()}
                                            </div>
                                            <div className="repository-forks">
                                                🍴 {repo.forks.toLocaleString()}
                                            </div>
                                        </div>
                                    </div>
                                    <div className="repository-description">{repo.description}</div>
                                    <div className="repository-footer">
                                        <div className="repository-language">
                                            <span
                                                className="language-color"
                                                style={{ backgroundColor: getLanguageColor(repo.language) }}
                                            ></span>
                                            <span className="language-name">{repo.language}</span>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default TopRepositories;
