import { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useGitHubData } from '../contexts/GitHubDataContext';
import { ExternalLinkIcon } from '@heroicons/react/outline';

// 简化版仪表盘组件
const Dashboard = () => {
    const {
        repositories,
        contributors,
        isLoading,
        lastUpdated
    } = useGitHubData();

    const [stats, setStats] = useState({
        totalRepos: 0,
        totalStars: 0,
        totalContributors: 0,
        topLanguages: [],
        trendingRepos: [],
        mostStarredRepos: []
    });

    // 计算汇总统计信息
    useEffect(() => {
        if (repositories.length) {
            // 计算总星标数
            const totalStars = repositories.reduce((sum, repo) => sum + repo.stars_count, 0);

            // 计算语言统计
            const languageCounts = repositories.reduce((acc, repo) => {
                if (repo.language) {
                    acc[repo.language] = (acc[repo.language] || 0) + 1;
                }
                return acc;
            }, {});

            // 转换为数组并排序
            const topLanguages = Object.entries(languageCounts)
                .map(([name, count]) => ({
                    name,
                    count,
                    percentage: (count / repositories.length * 100).toFixed(1)
                }))
                .sort((a, b) => b.count - a.count)
                .slice(0, 8);

            // 获取最近一个月趋势仓库
            const monthAgo = new Date();
            monthAgo.setMonth(monthAgo.getMonth() - 1);

            const trendingRepos = [...repositories]
                .sort((a, b) => {
                    const aGrowth = a.stars_count - (a.stars_one_month_ago || 0);
                    const bGrowth = b.stars_count - (b.stars_one_month_ago || 0);
                    return bGrowth - aGrowth;
                })
                .slice(0, 5)
                .map(repo => ({
                    ...repo,
                    growth: repo.stars_count - (repo.stars_one_month_ago || 0)
                }));

            // 获取星标最多的仓库
            const mostStarredRepos = [...repositories]
                .sort((a, b) => b.stars_count - a.stars_count)
                .slice(0, 5);

            // 计算唯一贡献者数量
            const uniqueContributors = new Set(
                contributors.map(contributor => contributor.login)
            ).size;

            setStats({
                totalRepos: repositories.length,
                totalStars,
                totalContributors: uniqueContributors,
                topLanguages,
                trendingRepos,
                mostStarredRepos
            });
        }
    }, [repositories, contributors]);

    // 格式化数字
    const formatNumber = (num) => {
        if (num >= 1000000) {
            return (num / 1000000).toFixed(1) + 'M';
        } else if (num >= 1000) {
            return (num / 1000).toFixed(1) + 'K';
        }
        return num.toString();
    };

    // 动画配置
    const containerVariants = {
        hidden: { opacity: 0 },
        visible: {
            opacity: 1,
            transition: {
                staggerChildren: 0.1
            }
        }
    };

    const itemVariants = {
        hidden: { y: 20, opacity: 0 },
        visible: {
            y: 0,
            opacity: 1,
            transition: { type: 'spring', stiffness: 100 }
        }
    };

    // 格式化日期
    const formatDate = (dateString) => {
        if (!dateString) return '';
        const date = new Date(dateString);
        return date.toLocaleDateString('zh-CN', {
            year: 'numeric',
            month: 'long',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    return (
        <div className="dashboard-page">
            <header className="dashboard-header">
                <h1>GitHub 热门仓库概览</h1>
                {lastUpdated && (
                    <p className="last-updated-text">
                        数据更新于: {formatDate(lastUpdated)}
                    </p>
                )}
            </header>

            {isLoading ? (
                <div className="loading-container">
                    <div className="spinner"></div>
                    <p>加载中...</p>
                </div>
            ) : (
                <motion.div
                    className="dashboard-content"
                    variants={containerVariants}
                    initial="hidden"
                    animate="visible"
                >
                    {/* 主要统计数据 */}
                    <motion.div className="stats-grid" variants={itemVariants}>
                        <div className="stat-card">
                            <h3>仓库总数</h3>
                            <div className="stat-value">{formatNumber(stats.totalRepos)}</div>
                        </div>

                        <div className="stat-card">
                            <h3>星标总数</h3>
                            <div className="stat-value">{formatNumber(stats.totalStars)}</div>
                        </div>

                        <div className="stat-card">
                            <h3>贡献者总数</h3>
                            <div className="stat-value">{formatNumber(stats.totalContributors)}</div>
                        </div>

                        <div className="stat-card">
                            <h3>平均星标</h3>
                            <div className="stat-value">
                                {formatNumber(Math.round(stats.totalStars / stats.totalRepos))}
                            </div>
                        </div>
                    </motion.div>

                    {/* 语言分布 */}
                    <motion.div className="panel languages-panel" variants={itemVariants}>
                        <h3>热门编程语言</h3>
                        <div className="language-bars">
                            {stats.topLanguages.map(lang => (
                                <div key={lang.name} className="language-bar-item">
                                    <div className="language-info">
                                        <span className="language-name">{lang.name}</span>
                                        <span className="language-count">{lang.count}个仓库</span>
                                    </div>
                                    <div className="language-bar-container">
                                        <div
                                            className="language-bar-fill"
                                            style={{
                                                width: `${lang.percentage}%`,
                                                backgroundColor: getLanguageColor(lang.name)
                                            }}
                                        />
                                    </div>
                                    <span className="language-percentage">{lang.percentage}%</span>
                                </div>
                            ))}
                        </div>
                    </motion.div>

                    <div className="panels-row">
                        {/* 趋势仓库 */}
                        <motion.div className="panel trending-panel" variants={itemVariants}>
                            <h3>月度趋势仓库</h3>
                            <div className="repos-list">
                                {stats.trendingRepos.map(repo => (
                                    <div key={repo.id} className="repo-item">
                                        <div className="repo-main-info">
                                            <h4 className="repo-name">{repo.name}</h4>
                                            <div className="repo-details">
                                                <span className="language-dot" style={{ backgroundColor: getLanguageColor(repo.language) }}></span>
                                                <span className="repo-language">{repo.language}</span>
                                                <span className="repo-stars">⭐ {formatNumber(repo.stars_count)}</span>
                                            </div>
                                        </div>
                                        <div className="repo-growth">
                                            +{formatNumber(repo.growth)}
                                            <span className="growth-period">本月</span>
                                        </div>
                                        <a
                                            href={repo.url}
                                            target="_blank"
                                            rel="noopener noreferrer"
                                            className="repo-link"
                                        >
                                            <ExternalLinkIcon className="icon" />
                                        </a>
                                    </div>
                                ))}
                            </div>
                        </motion.div>

                        {/* 最受欢迎仓库 */}
                        <motion.div className="panel popular-panel" variants={itemVariants}>
                            <h3>最受欢迎仓库</h3>
                            <div className="repos-list">
                                {stats.mostStarredRepos.map(repo => (
                                    <div key={repo.id} className="repo-item">
                                        <div className="repo-main-info">
                                            <h4 className="repo-name">{repo.name}</h4>
                                            <div className="repo-details">
                                                <span className="language-dot" style={{ backgroundColor: getLanguageColor(repo.language) }}></span>
                                                <span className="repo-language">{repo.language}</span>
                                                <span className="repo-contributors">👥 {repo.contributors_count || 0}</span>
                                            </div>
                                        </div>
                                        <div className="repo-stars-large">
                                            ⭐ {formatNumber(repo.stars_count)}
                                        </div>
                                        <a
                                            href={repo.url}
                                            target="_blank"
                                            rel="noopener noreferrer"
                                            className="repo-link"
                                        >
                                            <ExternalLinkIcon className="icon" />
                                        </a>
                                    </div>
                                ))}
                            </div>
                        </motion.div>
                    </div>
                </motion.div>
            )}
        </div>
    );
};

// 根据语言获取颜色
const getLanguageColor = (language) => {
    if (!language) return '#999';

    const colorMap = {
        JavaScript: '#f1e05a',
        TypeScript: '#2b7489',
        Python: '#3572A5',
        Java: '#b07219',
        Go: '#00ADD8',
        Rust: '#dea584',
        C: '#555555',
        'C++': '#f34b7d',
        'C#': '#178600',
        Ruby: '#701516',
        PHP: '#4F5D95',
        Swift: '#ffac45',
        Kotlin: '#F18E33',
        Dart: '#00B4AB',
        Unknown: '#999999'
    };

    return colorMap[language] || `hsl(${Math.abs(language.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0)) % 360}, 70%, 60%)`;
};

export default Dashboard;
