import { useState, useEffect, useRef } from 'react';
import { motion, useInView } from 'framer-motion';
import API from '../config/api';

// 根据语言名称生成颜色
const getLanguageColor = (language) => {
    const colorMap = {
        JavaScript: '#f1e05a',
        TypeScript: '#2b7489',
        Python: '#3572A5',
        Java: '#b07219',
        Go: '#00ADD8',
        'C++': '#f34b7d',
        Ruby: '#701516',
        PHP: '#4F5D95',
        Rust: '#dea584',
        'C#': '#178600',
        Swift: '#ffac45',
        Kotlin: '#F18E33',
        'C': '#555555',
        'Shell': '#89e051',
        'HTML': '#e34c26',
        'CSS': '#563d7c',
    };
    if (colorMap[language]) {
        return colorMap[language];
    }
    const hue = Math.abs(language.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0)) % 360;
    return `hsl(${hue}, 65%, 65%)`;
};

// 新增：动画列表项组件
const AnimatedLanguageItem = ({ language, count, percentage, barWidth, color, index, onMouseEnter, onClick, isSelected }) => {
    const ref = useRef(null);
    const inView = useInView(ref, { amount: 0.5, triggerOnce: false });// 判断元素是否在视图中

    return (
        <motion.div
            ref={ref}// 获取元素的引用
            data-index={index}// 设置元素的索引
            onMouseEnter={onMouseEnter}// 鼠标进入元素时触发
            onClick={onClick}// 点击元素时触发
            initial={{ scale: 0.7, opacity: 0 }}// 初始状态
            animate={inView ? { scale: 1, opacity: 1 } : { scale: 0.7, opacity: 0 }}// 动画状态
            transition={{ duration: 0.1, delay: index * 0.01 }}// 动画持续时间，延迟时间
            className={`language-bar-item ${isSelected ? 'selected' : ''}`}
        >
            <div className="language-name">{language}</div>
            <div className="bar-container">
                <div
                    className="bar-fill"
                    style={{
                        width: barWidth,
                        height: '20px',
                        display: 'block',
                        opacity: 0.8,
                        backgroundColor: color,
                    }}
                />
            </div>
            <div className="language-count">
                {count.toLocaleString()} ({percentage}%)
            </div>
            {hoveredLanguage && topRepos[hoveredLanguage] && (
                <div className="language-top-repos">
                    <h4>{hoveredLanguage} 热门仓库</h4>
                    <ul>
                        {topRepos[hoveredLanguage].map((repo, i) => (
                            <li key={i}>
                                <div className="repo-name">{repo.repo_name}</div>
                                <div className="repo-stats">
                                    <span>⭐ {repo.stars.toLocaleString()}</span>
                                    <span>🔱 {repo.forks.toLocaleString()}</span>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </motion.div>
    );
};

const Dashboard = () => {
    const [stats, setStats] = useState({ totalRepositories: 0, totalStars: 0, totalContributors: 0 });
    const [languageData, setLanguageData] = useState([]);
    const [topRepos, setTopRepos] = useState({});
    const [hoveredLanguage, setHoveredLanguage] = useState(null);
    const [loadingStats, setLoadingStats] = useState(true);
    const [loadingLanguages, setLoadingLanguages] = useState(true);
    const [error, setError] = useState(null);
    const [selectedLanguageIndex, setSelectedLanguageIndex] = useState(-1);

    useEffect(() => {
        const fetchDashboardData = async () => {
            try {
                setStats({ totalRepositories: 5427, totalStars: 3641982, totalContributors: 48273 });
                setLoadingStats(false);

                setLoadingLanguages(true);
                const langResponse = await fetch(API.languageDistribution);
                if (!langResponse.ok) {
                    throw new Error(`获取语言数据失败: ${langResponse.status}`);
                }
                const langResult = await langResponse.json();
                setLanguageData(langResult);

                const languageTopRepos = {};
                const topLanguages = langResult.slice(0, 15);
                for (const lang of topLanguages) {
                    try {
                        const reposResponse = await fetch(`${API.languageTopRepos}?language=${encodeURIComponent(lang.language)}`);
                        if (reposResponse.ok) {
                            const reposData = await reposResponse.json();
                            languageTopRepos[lang.language] = reposData;
                        }
                    } catch (repoError) {
                        console.warn(`获取 ${lang.language} 热门仓库失败:`, repoError);
                    }
                }
                setTopRepos(languageTopRepos);

            } catch (err) {
                setError(`获取仪表盘数据失败: ${err.message}`);
                console.error(err);
            } finally {
                setLoadingLanguages(false);
            }
        };

        fetchDashboardData();
    }, []);

    const totalCount = languageData.reduce((sum, item) => sum + item.count, 0);
    const maxCount = Math.max(...languageData.map(item => item.count));

    return (
        <div className="dashboard">
            <h1 className="page-title">数据概览</h1>

            <div className="stats-overview">
                <div className="card stat-card">
                    <div className="stat-value">{loadingStats ? '...' : stats.totalRepositories.toLocaleString()}</div>
                    <div className="stat-label">仓库总数</div>
                </div>
                <div className="card stat-card">
                    <div className="stat-value">{loadingStats ? '...' : stats.totalStars.toLocaleString()}</div>
                    <div className="stat-label">总星标数</div>
                </div>
                <div className="card stat-card">
                    <div className="stat-value">{loadingStats ? '...' : stats.totalContributors.toLocaleString()}</div>
                    <div className="stat-label">贡献者总数</div>
                </div>
            </div>

            {error && <div className="error-message">错误: {error}</div>}

            <div className="dashboard-row">
                <div className="card language-distribution-card">
                    <div className="card-header">
                        <h2 className="card-title">编程语言分布</h2>
                    </div>
                    <div className="card-body">
                        {loadingLanguages ? (
                            <div className="loading-container">正在加载语言数据...</div>
                        ) : !languageData || languageData.length === 0 ? (
                            <div className="empty-data">没有找到语言分布数据</div>
                        ) : (
                            <div className="scroll-list-container">
                                <div className="scroll-list">
                                    {languageData.slice(0, 15).map((item, index) => {
                                        const percentage = totalCount > 0 ? (item.count / totalCount * 100).toFixed(1) : 0;
                                        const barWidth = maxCount > 0 ? (item.count / maxCount * 100).toFixed(1) + '%' : '0%';

                                        return (
                                            <AnimatedLanguageItem
                                                key={index}
                                                index={index}
                                                language={item.language}
                                                count={item.count}
                                                percentage={percentage}
                                                barWidth={barWidth}
                                                color={getLanguageColor(item.language)}
                                                isSelected={selectedLanguageIndex === index}
                                                onMouseEnter={() => {
                                                    setHoveredLanguage(item.language);
                                                    setSelectedLanguageIndex(index);
                                                }}
                                                onClick={() => {
                                                    setHoveredLanguage(item.language);
                                                    setSelectedLanguageIndex(index);
                                                }}
                                            />
                                        );
                                    })}
                                </div>
                            </div>
                        )}

                        {/* 显示选中语言的热门仓库 */}
                        {hoveredLanguage && topRepos[hoveredLanguage] && (
                            <div className="language-top-repos">
                                <h4>{hoveredLanguage} 热门仓库</h4>
                                <ul>
                                    {topRepos[hoveredLanguage].map((repo, i) => (
                                        <li key={i}>
                                            <div className="repo-name">{repo.repo_name}</div>
                                            <div className="repo-stats">
                                                <span>⭐ {repo.stars.toLocaleString()}</span>
                                                <span>🔱 {repo.forks.toLocaleString()}</span>
                                            </div>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        )}
                    </div>
                </div>

                <div className="card top-repositories-card">
                    <div className="card-header">
                        <h2 className="card-title">热门仓库</h2>
                    </div>
                    <div className="card-body">
                        <ul className="repo-list">
                            {[
                                { name: 'freeCodeCamp/freeCodeCamp', stars: 380000, description: 'The freeCodeCamp.org open-source codebase and curriculum.' },
                                { name: 'vuejs/vue', stars: 210000, description: '🖖 Vue.js is a progressive, incrementally-adoptable JavaScript framework for building UI on the web.' },
                                { name: 'facebook/react', stars: 200000, description: 'A declarative, efficient, and flexible JavaScript library for building user interfaces.' }
                            ].map(repo => (
                                <li key={repo.name} className="repo-item">
                                    <div className="repo-header">
                                        <h3 className="repo-name">{repo.name}</h3>
                                        <div className="repo-stars">
                                            ⭐ {repo.stars.toLocaleString()}
                                        </div>
                                    </div>
                                    <p className="repo-description">{repo.description}</p>
                                </li>
                            ))}
                        </ul>
                        <p style={{ fontSize: '0.8em', color: 'var(--light-text-tertiary)', marginTop: '10px' }}>注：此热门仓库列表为静态示例数据。</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
